                messageList.Add("アイン：よし、広い部屋に出たな。"); eventList.Add(ActionEvent.None);

                messageList.Add("アイン：看板があるみたいだな、少し調べてみるか。"); eventList.Add(ActionEvent.None);
            }
        }

